/**
 * @param {string} beginWord
 * @param {string} endWord
 * @param {string[]} wordList
 * @return {string[][]}
 */
var findLadders = function (beginWord, endWord, wordList) {
    let possibleJumps = getPossibleJumps(beginWord, wordList)
    if (!(endWord in possibleJumps)) return [];
    let backtrackMap = bfsFindPath(endWord, beginWord, possibleJumps);
    let result = [];
    dfsFindPath(beginWord, endWord, [], backtrackMap, possibleJumps, result)
    return result;

};
function dfsFindPath(currentWord, endWord, wordStack, backtrackMap, possibleJumps, result) {
    wordStack.push(currentWord);
    if (currentWord === endWord) {
        result.push(wordStack.slice());
    } else {
        let currentDistanceToEnd = backtrackMap.get(currentWord);
        for (let nextWord of possibleJumps[currentWord]) {
            if (backtrackMap.get(nextWord) === currentDistanceToEnd - 1) {
                dfsFindPath(nextWord, endWord, wordStack, backtrackMap, possibleJumps, result);
            }
        }
    }
    wordStack.pop();
}

function bfsFindPath(beginWord, endWord, possibleJumps) {
    let queue = new Queue();
    let bfsNode = {
        word: beginWord,
        jumps: 0,
    };
    queue.enqueue(bfsNode);
    let found = false;
    let backtrackMap = new Map();
    let visited = new Set();
    backtrackMap.set(beginWord, 0);


    while (queue.size() > 0) {
        let size = queue.size();
        for (let i = 0; i < size; i++) {
            bfsNode = queue.dequeue();
            visited.add(bfsNode.word);
            //Found it!
            if (bfsNode.word === endWord) {
                found = true;
            } else {
                // let neighboorsNodes = possibleJumps[bfsNode.word];
                // for (let j = 0; j < neighboorsNodes.length; j++) {
                for (let neighboor of possibleJumps[bfsNode.word]) {
                    if (backtrackMap.has(neighboor)) {
                        continue;
                    } else {
                        backtrackMap.set(neighboor, bfsNode.jumps + 1);
                        const neighboorNode = {
                            word: neighboor,
                            jumps: bfsNode.jumps + 1
                        };
                        queue.enqueue(neighboorNode);
                    }
                }
            }
        }
        if (found) break;
    }
    return backtrackMap;
}


function getPossibleJumps(beginWord, wordList) {
    let jumpsMap = {};
    wordList.push(beginWord);

    for (let i = 0; i < wordList.length; i++) {
        let currentWord = wordList[i];
        if (currentWord in jumpsMap) continue; // Duplicates
        else jumpsMap[currentWord] = new Set();
        for (let j = 0; j < wordList.length; j++) {
            if (j === i) continue;
            if (getDiff(currentWord, wordList[j]) === 1) {
                jumpsMap[currentWord].add(wordList[j])
            }
        }
    }
    return jumpsMap
}
function getDiff(word1, word2) {
    let diff = 0;
    for (let i = 0; i < word1.length; i++) {
        if (word1[i] !== word2[i]) diff++;
        if (diff > 1) return diff;
    }
    return diff;
}


